import React, { Component } from 'react';

import Ingrediente from './Ingrediente';

class Form extends Component {

    constructor(props){
        super(props);
        this.state = {
            nombre: "",
            calorias: 0,
            ingredientes: []
        }
    }


    //Function to handle the changes in the form and update the state
    inputChange = (event) => {
        this.setState({ [event.target.name]: event.target.value });
    }

    //This function catch the data of news ingredients of the Ingredient component 
    //and update the state to add news ingredients
    addIngredient(ing, cant){
        let newing = {
            ingrediente: ing,
            cantidad: cant
        }
        this.setState({
            ingredientes: [...this.state.ingredientes, newing]
        });
    }

    //Function to add a new recipe in the main
    addRecipe = () => {
        if(this.state.nombre === "" || this.state.ingredientes.length === 0){
            alert('Debes debes de llenar todos los campos para poder agregar una nueva receta');
        }else{
            this.props.new(this.state);
        }
    }



    render(){
        //Funsion to delete an ingredient of the list
        function deleteIngredient(key){
            console.log(key);
            //console.log(key.target)
        }

        return(
            <section>
                <h1>Agregar una nueva receta</h1>
                <form>
                    <label htmlFor="name">Nombre</label>
                    <br/>
                    <input name="nombre" 
                    onChange={this.inputChange} 
                    value={this.state.nombre}/>
                    <br/>
                    <label htmlFor="calorias">Calorias</label>
                    <br/>
                    <input name='calorias' value={this.state.calorias}
                    onChange={this.inputChange} type="number"/>
                    <h4>Ingredientes:</h4>
                </form>
                <ul>         
                {
                    this.state.ingredientes.map((ingrediente, index) => {
                        return(
                            <li key={index}>
                                <span>{ingrediente.ingrediente}</span>
                                <span> - </span>
                                <span>{ingrediente.cantidad} </span>
                                <button type="button" 
                                style={{background: 'red', color: "white"}}
                                onClick={deleteIngredient(index)}> X </button>
                            </li>
                        );
                    })
                }
                </ul>
                <br/>
                <button onClick={this.addRecipe}>Agregar receta</button>
                <Ingrediente add={this.addIngredient.bind(this)}/>
            </section>
        )
    }
}

export default Form;
