import React from 'react';
import { useHistory } from 'react-router-dom';

const Recipe = (props) => {
    const {id, calorias, nombre, ingredientes} = props;
    const history = useHistory();

    //Method to edit the recipe
    const handleEdit = () => {
		history.push('/agregar', props);
    };

    //Method to delete the recipe
    const handleDelete = () => {
        this.props.delete(id);
		//history.push('/agregar');
		//console.log(id);
    };


    return(
        <div className='card' style={{marginBottom: '24px'}}>
            <div className="card-body">
                <h4 className="card-title">Receta para hacer {nombre}</h4>
                <span className="card-text">Calorias: {calorias}</span>
                <br />

            </div>
            <ul className="list-group list-group-flush">
                <li className="list-group-item">
                    Ingredientes:
                </li>
                {
                    ingredientes.map((single, i) => {
                        return (
                            <li key={i} className="list-group-item">
                                {single.ingrediente} : {single.cantidad}
                            </li>							
                        )
                    })
                }
            </ul>
            <button className="btn btn-outline-primary"
            onClick={handleEdit}>Editar</button>
            <button className="btn btn-primary"
            onClick={handleDelete}> Eliminar</button> 
        </div>
    )
}

export default Recipe;
