import React, { Component } from 'react';

import Ingrediente from './Ingrediente';

class Form extends Component {

    status = {
        nombre: '',
        ingredientes: []
    }

    inputChange = (e) => {
        console.log(e);
    }

    addIngredient(ing, cant){
        const newing = {
            ingrediente: ing,
            cantidad: cant
        }
        this.setState({
            ingredientes: [...this.state.ingredientes, newing]
        });
        console.log(this.state);
    }

    render(){
        return(
            <section>
                <h1>Agregar una nueva receta</h1>
                <form>
                    <label htmlFor="name">Nombre</label>
                    <input name="nombre" onChange={this.inputChange} />
                    <br/>
                    <h4>Ingredientes:</h4>
                </form>
                <button>Agregar un nuevo ingrediente</button>
                <Ingrediente add={this.addIngredient}/>
            </section>
        )
    }
}

export default Form;
