import React from 'react';
import { useHistory } from 'react-router-dom';

const Recipe = (props) => {
    const {id, calorias, nombre, ingredientes} = props;
    const history = useHistory();

    //Function to edit the recipe
    const handleEdit = () => {
		history.push('/agregar', {id, calorias, nombre, ingredientes});
		//console.log(id);
	};

    return(
        <div className='card'>
            <div className="card-body">
                <h4 className="card-title">Receta para hacer {nombre}</h4>
                <span className="card-text">Calorias: {calorias}</span>
                <br />

            </div>
            <ul className="list-group list-group-flush">
                <li className="list-group-item">
                    Ingredientes:
                </li>
                {
                    ingredientes.map((single, i) => {
                        return (
                            <li key={i} className="list-group-item">
                                {single.ingrediente} : {single.cantidad}
                            </li>							
                        )
                    })
                }
            </ul>
            <button className="btn btn-outline-primary"
            onClick={handleEdit()}>Editar</button>
            <button className="btn btn-primary"
           > Eliminar</button> 
        </div>
    )
}

export default Recipe;
