import React, { Component } from 'react';

class Form extends Component {

    status = {
        nombre: '',
        ingredientes: [{
            ingrediente: "",
            cantidad: 0
        }]
    }

    inputChange = (e) => {
        console.log(e);
    }

    render(){
        return(
            <section>
                <h1>Agregar una nueva receta</h1>
                <form>
                    <label htmlFor="name">Nombre</label>
                    <input name="nombre" onChange={this.inputChange} />
                    <br/>
                    <h4>Ingredientes:</h4>
                    <button>Agregar un nuevo ingrediente</button>
                    {/* <label for='ingrediente'>Ingrediente</label> */}
                </form>
            </section>
        )
    }
}

export default Form;
