import React, { Component } from 'react';

import Ingrediente from './Ingrediente';

class Form extends Component {

    state = {
        nombre: "",
        ingredientes: []
    }

    inputChange = (event) => {
        this.setState({ nombre: event.target.value });
    }

    addIngredient(ing, cant){
        let newing = {
            ingrediente: ing,
            cantidad: cant
        }
        console.log(newing);
        this.setState({
            ingredientes: [...this.state.ingredientes, newing] //...this.state.ingredientes, this function not work yet
        });
    }

    render(){
        return(
            <section>
                <h1>Agregar una nueva receta</h1>
                <form>
                    <label htmlFor="name">Nombre</label>
                    <input name="nombre" 
                    onChange={this.inputChange} 
                    value={this.state.nombre}/>
                    <br/>
                    <h4>Ingredientes:</h4>
                </form>
                {
                    this.state.ingredientes.map((ingrediente, index) => {
                        return(
                            <section key={index}>
                                <span>{ingrediente.ingrediente}</span>
                                <span>{ingrediente.cantidad}</span>
                            </section>
                        );
                    })
                }
                <br/>
                <button>Agregar receta</button>
                <Ingrediente add={this.addIngredient.bind(this)}/>
            </section>
        )
    }
}

export default Form;
