import React, { Component } from 'react';

import Ingrediente from './Ingrediente';

class Form extends Component {

    status = {
        nombre: '',
        ingredientes: [{}]
    }

    inputChange = (e) => {
        console.log(e);
    }

    render(){
        return(
            <section>
                <h1>Agregar una nueva receta</h1>
                <form>
                    <label htmlFor="name">Nombre</label>
                    <input name="nombre" onChange={this.inputChange} />
                    <br/>
                    <h4>Ingredientes:</h4>
                </form>
                <button>Agregar un nuevo ingrediente</button>
                <Ingrediente />
            </section>
        )
    }
}

export default Form;
