import React, { Component } from 'react';

class Ingrediente extends Component {

    constructor(props){
        super(props);
        this.state ={
            ingrediente: "",
            cantidad: "",
            error: null
        }
        this.handleChange = this.handleChange.bind(this);
        this.onSubhandleSubmitit = this.handleSubmit.bind(this);
    }


    //Fuction to handle the change of the inputs and update the state
    handleChange(event){
        this.setState({[event.target.name]: event.target.value});
    }

    //Function to send the data of ingredients to the recipes
    handleSubmit(event){
        event.preventDefault();
        if(this.state.ingrediente === "" || this.state.cantidad === ''){
            alert("Tienes que llenar los campos para poder agregar un nuevo ingrediente");
        }
        else{
            const id = `id${this.state.ingrediente}-${String(this.state.cantidad)}-${Date.now()}`;
            this.props.add(this.state.ingrediente, this.state.cantidad, id);
            this.setState({ingrediente: '', cantidad: ''});
        }
    }

    render(){
        return(
            <form onSubmit={this.handleSubmit}>
                <hr/>
                <label htmlFor="ingrediente">Ingrediente </label>
                <input name="ingrediente" 
                type="text"
                onChange={this.handleChange} 
                value={this.state.ingrediente}
                className={this.state.error ? 'is-invalid' : 'is-valid'}
                />
                <br/>
                <br/>
                <label htmlFor="cantidad">Cantidades </label>
                <input name="cantidad"
                type='text'
                onChange={this.handleChange} 
                value={this.state.cantidad}
                />
                <br/>
                <br/>
                <input type="submit" value="Agregar ingrediente"/>
            </form>
        )
    }
}

export default Ingrediente;
