import React from 'react';

var Recipe = (props) => {

    return(
        <React.Fragment  key={receta.id}>
            <br/>
            <div className='card'>
                <div className="card-body">
                    <h4 className="card-title">Receta para hacer {receta.nombre}</h4>
                    <span className="card-text">Calorias: {receta.calorias}</span>
                    <br />

                </div>
                <ul className="list-group list-group-flush">
                    <li className="list-group-item">
                        Ingredientes:
                    </li>
                    {
                        receta.ingredientes.map((single, i) => {
                            return (
                                <li key={i} className="list-group-item">
                                    {single.ingrediente} : {single.cantidad}
                                </li>							
                            )
                        })
                    }
                </ul>
                <button className="btn btn-outline-primary"
                onClick={() => {this.handleEdit(receta.id)}}>Editar</button>
                <button className="btn btn-primary"
                onClick={() => {this.handleDelete(receta.id)}}> Eliminar</button>
            </div>
        </React.Fragment>
    )
}

export default Recipe;
