import React, {Component} from 'react';
import './assets/App.css';
import {BrowserRouter as Router, Route, Link } from 'react-router-dom';

//Components
import Receta from './component/Receta';
import Form from './component/Form';


class App extends Component {

  state = {
    recetas: [
      {
        nombre: 'Pizza',
        Ingredientes:[
            {
                ingrediente: 'tomate',
                cantidad: 5
            },
            {
                ingrediente: 'queso',
                cantidad: 8
            },
            {
                ingrediente: 'masa',
                cantidad: 2
            }
        ],
        calorias: 300
      }
    ]
  };

  //This function is for catch a new recipe and update the list in the state
  nuevaReceta(object){
    this.setState({
      recetas: [...this.state.recetas, object]
    });
    console.log(this.state);
  }

  render(){
    return(
      <section>
        <Router>
          <Link to="/receta">Receta  -   </Link>
          <Link to="/agregar">Agregar Receta</Link>

          <Route path="/receta" render={ () => <Receta recetas={this.state.recetas}/>}/>
          <Route path="/agregar" render={() => <Form new={this.nuevaReceta.bind(this)} />}/>
        </Router>
      </section>
    )
  };
}

export default App;
