import React, {Component} from 'react';
import { Link } from 'react-router-dom';

class Receta extends Component{

	render(){
		return(
			<section>
				<h1>Lista de recetas</h1>
				{
					this.props.recetas.map((receta, index) => {
						return(
							<React.Fragment  key={index}>
							<br/>
							<div className='card'>
								<div className="card-body">
									<h4 className="card-title">Receta para hacer {receta.nombre}</h4>
									<span className="card-text">Calorias: {receta.calorias}</span>
								</div>
								<ul className="list-group list-group-flush">
									{
										receta.ingredientes.map((single, i) => {
											return (
												<li key={i} className="list-group-item">
													{single.ingrediente} : {single.cantidad}
												</li>							
											)
										})
									}
								</ul>
							</div>

							</React.Fragment>

						);
					})
				}
				<br/>
				<Link to="/agregar" className='btn btn-primary'>Agregar nueva receta</Link>
			</section>
		);
	}
}

export default Receta;
