import React from 'react';

var Recipe = ({id, calorias, nombre, ingredientes}) => {

    //Function to edit the recipe
    handleEdit = () => {
		this.props.history.push('/agregar', {id, calorias, nombre, ingredientes});
		//console.log(id);
	};

    return(
        <div className='card'>
            <div className="card-body">
                <h4 className="card-title">Receta para hacer {nombre}</h4>
                <span className="card-text">Calorias: {calorias}</span>
                <br />

            </div>
            <ul className="list-group list-group-flush">
                <li className="list-group-item">
                    Ingredientes:
                </li>
                {
                    ingredientes.map((single, i) => {
                        return (
                            <li key={i} className="list-group-item">
                                {single.ingrediente} : {single.cantidad}
                            </li>							
                        )
                    })
                }
            </ul>
            <button className="btn btn-outline-primary"
            onClick={() => {this.handleEdit(id)}}>Editar</button>
            <button className="btn btn-primary"
            onClick={() => {this.handleDelete(id)}}> Eliminar</button>
        </div>
    )
}

export default Recipe;
