import React, {Component} from 'react';

class Receta extends Component{

	render(){
		var receta = {
			nombre: 'Pizza',
			Ingredientes:[
				{
					nombre: 'tomate',
					cantidad: 5
				},
				{
					nombre: 'queso',
					cantidad: 8
				},
				{
					nombre: 'masa',
					cantidad: 2
				}
			],
			calorias: 300
		};


		return(
			<section>
				<h1>Receta de : {receta.nombre}</h1>
				<h3>Calorias: {receta.calorias}</h3>
				<ol>
					{
						receta.Ingredientes.map((single, i) => {
							return (
								<li key={i}>{single.nombre}: {single.cantidad}</li>							
							);
						})
					}
				</ol>
			</section>
		);
	}
}

export default Receta;
