import React, {Component} from 'react';
import './assets/App.css';
import {BrowserRouter as Router, Route, Link, Switch } from 'react-router-dom';

//Components
import Receta from './component/Receta';
import Form from './component/Form';
import NotFound from './component/NotFound';

class App extends Component {

  constructor(props){
    super(props)
    this.state = {
      recetas: [
        {
          nombre: 'Pizza',
          ingredientes:[
              {
                  ingrediente: 'tomate',
                  cantidad: 5
              },
              {
                  ingrediente: 'queso',
                  cantidad: 8
              },
              {
                  ingrediente: 'masa',
                  cantidad: 2
              }
          ],
          calorias: 300
        }
      ]
    };

    this.nuevaReceta = this.nuevaReceta.bind(this);
  }


  //This function is for catch a new recipe and update the list in the state
  nuevaReceta(object){
    this.setState({
      recetas: [...this.state.recetas, object]
    });
  }

  render(){
    return(
      <section style={{textAlign: 'center'}}>
        <Router basename='/receta'>
          <Link to="/receta">Recetas  -   </Link>
          <Link to="/agregar">Agregar Receta</Link>

          <Switch>
            <Route path="/receta" render={ () => <Receta recetas={this.state.recetas}/>}/>
            <Route path="/agregar" render={() => <Form new={this.nuevaReceta} />}/>
            <Route component={NotFound}/>
          </Switch>
        </Router>
      </section>
    ) 
  };
}

export default App;
