import React from 'react';
import './assets/App.css';
import {BrowserRouter as Router, Route } from 'react-router-dom';

//Components
import Receta from './component/Receta';
import Header from './component/header/Header.js'


function App() {

  var state = {
    recetas: [
      {
        nombre: 'Pizza',
        Ingredientes:[
            {
                nombre: 'tomate',
                cantidad: 5
            },
            {
                nombre: 'queso',
                cantidad: 8
            },
            {
                nombre: 'masa',
                cantidad: 2
            }
        ],
        calorias: 300
    }
    ]
  }

  return (
    <section>
      <Header />
      <Router>
        <Route path="/receta" render={ () => {
          return <section>
              <h1>Seccion de recetas</h1>
              <Receta recetas={this.state.recetas}/>
            </section>
        }}/>
      </Router>
    </section>
  );
}

export default App;
