import React, { Component } from 'react';
import { useHistory } from 'react-router-dom';

import Ingrediente from './Ingrediente';

class Form extends Component {

    constructor(props){
        super(props);
        this.state = {
            nombre: "",
            calorias: 0,
            ingredientes: [],
            error: null
        }
        this.deleteIngredient = this.deleteIngredient.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.addIngredient = this.addIngredient.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }


    //Function to handle the changes in the form and update the state
    handleChange(event){
        this.setState({ 
            [event.target.name]: event.target.value,
            error: null 
        });
    }

    //This function catch the data of news ingredients of the Ingredient component 
    //and update the state to add news ingredients
    addIngredient(ing, cant, id){
        let newing = {
            id: id,
            ingrediente: ing,
            cantidad: cant
        }
        this.setState({
            ingredientes: [...this.state.ingredientes, newing]
        });
    }

    //Function to add a new recipe in the main
    handleSubmit(){
        if(this.state.nombre === "" || this.state.ingredientes.length === 0){
            this.setState({
                error: true
            })
            alert('Debes debes de llenar todos los campos para poder agregar una nueva receta y tener al menos un igrediente');
        }else{
            this.props.new(this.state);
            alert('Se ha agregado una nueva receta');
            //this.props.history.push('/');
            //this.props.history.push({to: '/'});
            //browserHistory.psuh()
            this.useHistory.push('/');
        }
    }

    //Funsion to delete an ingredient of the list
    deleteIngredient(id){
        this.setState({
            ingredientes: this.state.ingredientes.filter(ig => ig.id !== id)
        });
    }

    render(){
        return(
            <section>
                <h1>Agregar una nueva receta</h1>
                <form>
                    <label htmlFor="name">Nombre</label>

                    <input name="nombre" 
                    className={this.state.error ? `is-invalid form-control` : `form-control`}
                    onChange={this.handleChange} 
                    value={this.state.nombre}/>
                    <label htmlFor="calorias">Calorias</label>
                    <input name='calorias' value={this.state.calorias}
                    className={this.state.error ? `is-invalid form-control` : `form-control`}
                    onChange={this.handleChange} type="number"/>
                    <h4>Ingredientes:</h4>
                </form>
                <ul className='list-group'>         
                {
                    this.state.ingredientes.map((ingrediente) => {
                        return(
                            <li 
                            key={ingrediente.id}
                            className="list-group-item">
                                <span>{ingrediente.ingrediente}</span>
                                <span> - </span>
                                <span>{ingrediente.cantidad} </span>
                                <button type="button" 
                                //style={{background: 'red', color: "white"}}
                                className='btn btn-outline-danger'
                                onClick={() => {this.deleteIngredient(ingrediente.id)}}>
                                    X
                                </button>
                            </li>
                        );
                    })
                }
                </ul>
                <br/>
                <button onClick={this.handleSubmit} className="btn btn-primary">Agregar receta</button>
                <Ingrediente add={this.addIngredient} history={this.props.history}/>
            </section>
        )
    }
}

export default Form;
