import React, {Component} from 'react';
import './assets/App.css';
import {BrowserRouter as Router, Route } from 'react-router-dom';

//Components
import Receta from './component/Receta';
import Header from './component/header/Header.js';
import Form from './component/Form';


class App extends Component {

  state = {
    recetas: [
      {
        nombre: 'Pizza',
        Ingredientes:[
            {
                ingrediente: 'tomate',
                cantidad: 5
            },
            {
                ingrediente: 'queso',
                cantidad: 8
            },
            {
                ingrediente: 'masa',
                cantidad: 2
            }
        ],
        calorias: 300
    }
    ]
  };

  render(){
    return(
      <section>
        <Header />
        <Router>
          <Route path="/receta" render={ () => {
            return <section>
                <h1>Seccion de recetas</h1>
                <Receta recetas={this.state.recetas}/>
              </section>
          }}/>
          <Route path="/agregar" component={Form}/>
        </Router>
      </section>
    )
  };
}

export default App;
