import React, {Component} from 'react';

class Receta extends Component{

	render(){
		return(
			<section>
				<h1>Lista de recetas</h1>
				{
					this.props.recetas.map((receta, index) => {
						return(
						<div key={index}>
							<h4>Receta para hacer {receta.nombre}</h4>
							<span>Calorias: {receta.calorias}</span>
							<ol>
								{
									receta.Ingredientes.map((single, i) => {
										return (
											<li key={i}>
											{single.ingrediente}: 
											{single.cantidad}
											</li>							
										);
									})
								}
							</ol>
						</div>
						)
					})
				}	
			</section>
		);
	}
}

export default Receta;
