import React, { Component } from 'react';

class Ingrediente extends Component {
    state ={
        ingrediente: "",
        cantidad: ''
    } 

    //Fuction to handle the change of the inputs and update the state
    inputHandler = (event) => {
        this.setState({[event.target.name]: event.target.value});
    }

    //Function to send the data of ingredients to the recipes
    onSubmit = (event) => {
        event.preventDefault();
        if(this.state.ingrediente === "" || this.state.cantidad === 0){
            alert("Tienes que llenar los campos para poder agregar un nuevo ingrediente");
        }
        else{
            this.props.add(this.state.ingrediente, this.state.cantidad);
            this.setState({ingrediente: '', cantidad: 0});
        }
    }

    render(){
        return(
            <form onSubmit={this.onSubmit}>
                <hr/>
                <label htmlFor="ingrediente">Ingrediente </label>
                <input name="ingrediente" 
                type="text"
                onChange={this.inputHandler} 
                value={this.state.ingrediente}
                />
                <br/>
                <br/>
                <label htmlFor="cantidad">Cantidad </label>
                <input name="cantidad"
                type='text'
                onChange={this.inputHandler} 
                value={this.state.cantidad}
                />
                <br/>
                <br/>
                <input type="submit" value="Agregar ingrediente"/>
            </form>
        )
    }
}

export default Ingrediente;
