import React, {Component} from 'react';
import './assets/App.css';
import { BrowserRouter as Router, Route, Link, Switch, Redirect } from 'react-router-dom';

//Components
import RecipeList from './pages/RecipeList';
import NewRecipe from './pages/NewRecipe';
import NotFound from './pages/NotFound';

class App extends Component {

  constructor(props){
    super(props)
    this.state = {
      recetas: [
        {
          id: 1,
          nombre: 'Pizza',
          ingredientes:[
              {
                id: 1,
                ingrediente: 'tomate',
                cantidad: 5
              },
              {
                id: 2,
                ingrediente: 'queso',
                cantidad: 8
              },
              {
                id: 3,
                ingrediente: 'masa',
                cantidad: 2
              }
          ],
          calorias: 300
        }
      ]
    };

    this.newRecipe = this.newRecipe.bind(this);
  }


  //This function is for catch a new recipe and update the list in the state
  newRecipe(object, isEdit){
    if(isEdit){
      let newRecipes = this.state.recetas.filter(recipe => recipe.id === object.id ? false : true);
      this.setState({
        recetas: [...newRecipes, object]
      });
    }else{
      this.setState({
        recetas: [...this.state.recetas, object]
      });
    }
  }

  //Method to delete a recipe
  //This is not the best way to do this.
  deleteRecipe = (id) => {
    const newList = this.state.recetas.filter(recipe => recipe.id !== id);
    console.log(newList); 
    // this.setState({
    //   recetas: r
    // });
  }

  render(){
    return(
      <section style={{textAlign: 'center', width: '50%'}} className={'mx-auto'}>
        <Router>
          <Link to="/receta">Recetas  -   </Link>
          <Link to="/agregar">Agregar Receta</Link>

          <Switch history={this.history}>
            <Redirect exact from="/" to="/receta" />
            <Route path="/receta" render={ () => <RecipeList recetas={this.state.recetas}/>}/>
            <Route path="/agregar" render={() => <NewRecipe new={this.newRecipe}/>}/>
            <Route component={NotFound}/>
          </Switch>
        </Router>
      </section>
    ) 
  };
}

export default App;
